<script lang="ts">
    import { gsap } from "gsap"
    import { onMount } from "svelte"

    let aperturePath: SVGPathElement

    onMount(() => {

        const timeline = gsap.timeline({paused: true, repeat: -1})

        timeline
            .to(aperturePath, {
                rotation: 180,
                scale: 2,
                transformOrigin: 'center',
                ease: 'power1.in',
                duration: 5,
            })
            .to(aperturePath, {
                rotation: 360,
                scale: 1,
                transformOrigin: 'center',
                ease: 'power4.out',
                duration: 2.5,
            })

        timeline.play()
    })
</script>

